JS_SUGGEST_SAFE = """
<script>
function debounce(fn, ms){var t; return function(){ var args=arguments; clearTimeout(t); t=setTimeout(function(){ fn.apply(null, args); }, ms); }; }
var input=document.getElementById('name-input');
var suggest=document.getElementById('suggest');
var form=document.getElementById('add-form');
var hiddenTicker=document.getElementById('ticker-hidden');

function render(items){
  if(!items || !items.length){ suggest.style.display='none'; suggest.innerHTML=''; return; }
  var html='';
  for(var i=0;i<items.length;i++){
    var it=items[i];
    html += "<div class='suggest-item' data-ticker='"+it.ticker+"' title='"+(it.longname||"")+"'>"
          + "<span>"+(it.display||it.ticker)+"</span>"
          + "<span class='badge'>"+(it.exch||"")+"</span>"
          + "</div>";
  }
  suggest.innerHTML=html;
  suggest.style.display='block';
  var elems=suggest.querySelectorAll('.suggest-item');
  for(var j=0;j<elems.length;j++){
    elems[j].addEventListener('click', function(e){
      var tk=this.getAttribute('data-ticker');
      hiddenTicker.value=tk;
      form.setAttribute('action','/portfolio-editor/add-confirm');
      form.submit();
    });
  }
}

var fetchSuggest = debounce(function(q){
  q=(q||'').trim();
  if(!q){ render([]); return; }
  fetch('/api/symbol-suggest?name=' + encodeURIComponent(q))
    .then(function(resp){ return resp.json(); })
    .then(function(data){ render(data.items||[]); })
    .catch(function(e){});
}, 180);

input.addEventListener('input', function(e){
  hiddenTicker.value='';
  form.setAttribute('action','/portfolio-editor/add');
  fetchSuggest(e.target.value);
});

input.addEventListener('keydown', function(e){
  if(e.key===','){ setTimeout(function(){ fetchSuggest(input.value); }, 0); }
});

